document.addEventListener('DOMContentLoaded',function(){let postContainer=document.querySelector('.PostContainer');if(postContainer.scrollHeight>postContainer.clientHeight){postContainer.style.overflowY='auto';}})
function redirectToPath(path){window.location.href=path;}
document.addEventListener('DOMContentLoaded',function(){const openButton=document.querySelector('.open');const postDiv=document.querySelector('.PostContainers');const closeButton=postDiv.querySelector('.close');openButton.addEventListener('click',function(){postDiv.style.display='block';});closeButton.addEventListener('click',function(){if(confirm('Are you sure you want to discard the post?')){postDiv.style.display='none';}});});document.addEventListener('DOMContentLoaded',function(){const imageInput=document.getElementById('imageInput');const imageContainer=document.getElementById('preview');imageInput.addEventListener('change',function(){const file=this.files[0];if(file){const reader=new FileReader();reader.onload=function(e){imageContainer.style.display='block';imageContainer.querySelector('img').src=e.target.result;}
reader.readAsDataURL(file);}else{imageContainer.style.display='none';imageContainer.querySelector('img').src='';}});});document.addEventListener('DOMContentLoaded',function(){const userInput=document.getElementById('userInput');const progressCircle=document.querySelector('.progress-circle .progress');const progressText=document.querySelector('.progress-circle .text');const postButton=document.querySelector('.postButton');const moreWordsMessage=document.querySelector('.moreWords');let alertShown=false;userInput.addEventListener('input',()=>{const text=userInput.value.trim();const words=text.split(/\s+/).filter(word=>word.length>0);const wordCount=words.length;const progress=Math.min(wordCount/60*100,100);const offset=94.2-(94.2*progress/100);progressCircle.style.strokeDashoffset=offset;progressText.textContent=`${Math.floor(progress)}%`;if(progress>=100){progressCircle.classList.add('over-limit');}else{progressCircle.classList.remove('over-limit');}
if(progress>=100){postButton.style.display='none';}else{postButton.style.display='block';}
if(progress>=100){moreWordsMessage.style.display='block';}else{moreWordsMessage.style.display='none';}});userInput.addEventListener('keydown',(event)=>{if(event.key==='Backspace'){const text=userInput.value.trim();const words=text.split(/\s+/).filter(word=>word.length>0);const wordCount=words.length;if(wordCount<=60){progressCircle.classList.remove('over-limit');postButton.style.display='block';moreWordsMessage.style.display='none';alertShown=false;}}});});document.addEventListener('DOMContentLoaded',function(){var swiper=new Swiper('.swiper-container',{slidesPerView:'auto',spaceBetween:10,grabCursor:true,navigation:{nextEl:'.swiper-button-next',prevEl:'.swiper-button-prev',},pagination:{el:'.swiper-pagination',clickable:true,},});});document.addEventListener('DOMContentLoaded',function(){$('.Comment-onPost').on('click',function(e){e.preventDefault();const post_id=$(this).attr('data-id');const targetDiv=document.querySelector(`.PostComments-forPosts[data-id="${post_id}"]`);if(targetDiv){if(targetDiv.style.display==='block'){targetDiv.style.display='none';}else{targetDiv.style.display='block';}}});});document.addEventListener('DOMContentLoaded',function(){const closeComments=document.getElementsByClassName('comment-close');const postComments=document.getElementsByClassName('PostComments-forPosts');for(let i=0;i<closeComments.length;i++){closeComments[i].addEventListener('click',function(){for(let j=0;j<postComments.length;j++){postComments[j].style.display='none';}});}});;